public class SwapTwoStrings {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String first = "shubham";
		String last = "singhal";
		String temp;
		
		temp = first;
		first = last;
		last =temp;
		
		System.out.println("Swapped string first = "+first+ " and last = "+last);
		
	}

}
